parameters.jsCode:
const bookData = $node["Get Book Context Concept Architect1"].json;
const dbContext = bookData.context_data || {};
const config = bookData.configuration || {};

const targetPagesRaw = $node["Webhook Router"].json.body.targetPages || config.target_pages || dbContext.target_pages;
const targetPages = parseInt(targetPagesRaw) || 100;
const chaptersRate = $node["Webhook Router"].json.body.configuration?.chaptersRate || config.chaptersRate || 10;
const wordsPerPage = config.words_per_page || 250;

// Persistent Chapter Count
let numChapters = bookData.target_chapters;
if (!numChapters || isNaN(numChapters)) {
  numChapters = Math.max(1, Math.floor(targetPages / chaptersRate));
}

const totalWords = targetPages * wordsPerPage;
const wordsPerChapter = Math.floor(totalWords / numChapters);

return {
  json: {
    target_pages: targetPages,
    total_words_target: totalWords,
    num_chapters: numChapters,
    words_per_chapter: wordsPerChapter,
    paragraphs_per_chapter: config.paragraphs_per_chapter || 5,
    debug_inputs: { targetPagesRaw, chaptersRate, config }
  }
}

