parameters.query:
UPDATE books SET context_data = jsonb_set(
  jsonb_set(
    context_data,
    '{messages}',
    COALESCE(context_data->'messages', '[]'::jsonb) || jsonb_build_array(jsonb_build_object('role', 'assistant', 'content', $1))
  ),
  '{blueprint}',
  COALESCE(context_data->'blueprint', '{}'::jsonb) || $2::jsonb
) WHERE id = $3::uuid;

parameters.jsCode:
const items = $input.all();
if (items.length === 0) return { html: "<h1>Libro Vuoto</h1>" };

const bookTitle = items[0].json.book_title;
let bodyHtml = "";

function mdToHtml(md) {
  if (!md) return "";
  return md
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    .replace(/### (.*?)\n/g, '<h3>$1</h3>')
    .replace(/## (.*?)\n/g, '<h2>$1</h2>')
    .replace(/# (.*?)\n/g, '<h1>$1</h1>')
    .replace(/\n/g, '<br>');
}

items.forEach(item => {
  bodyHtml += `<section class="chapter"><h2>${item.json.chapter_title}</h2><div class="content">${mdToHtml(item.json.content)}</div></section>`;
});

const cleanHtml = `<div style="font-family: 'Georgia', serif; color: #333;"><h1 style="text-align: center; margin-bottom: 40pt;">${bookTitle}</h1>${bodyHtml}</div>`;

return { json: { html: cleanHtml, title: bookTitle } };

