// Estrai i dati del libro
const book = $input.first().json;
const context = book.context_data || {};
const blueprint = context.blueprint || {};
const selectedConcept = context.selected_concept || {};

// Helper per troncare il testo
const truncate = (str, n) => (str && str.length > n) ? str.substring(0, n-1) + '...' : str;

// Costruisci un prompt dettagliato per la cover
const genreStyles = {
  'fantasy': 'epic fantasy art, magical atmosphere, mystical elements',
  'thriller': 'dark moody lighting, suspenseful composition, cinematic',
  'romance': 'soft romantic lighting, dreamy atmosphere, elegant composition',
  'sci-fi': 'futuristic, sci-fi concept art, technological elements',
  'mystery': 'noir atmosphere, mysterious shadows, intriguing composition',
  'horror': 'dark horror aesthetic, eerie atmosphere, unsettling mood'
};

const baseStyle = genreStyles[book.genre?.toLowerCase()] || 'professional book cover art';
const rawSpecificStyle = selectedConcept.style || blueprint.plot_vibes || baseStyle;
const specificStyle = truncate(rawSpecificStyle, 200);

const rawThemes = blueprint.themes || 'Adventure and discovery';
const themes = truncate(rawThemes, 200);

const rawMood = blueprint.plot_vibes || 'Captivating and immersive';
const mood = truncate(rawMood, 200);

const targetAudience = context.target_audience || 'general readers';

// Crea il prompt ottimizzato
const coverPrompt = `Professional book cover design for "${book.title || 'Untitled Book'}"

Style: ${baseStyle}. ${specificStyle}
Genre: ${book.genre || 'Fiction'}
Themes: ${themes}
Mood: ${mood}

Visual elements:
- High-quality, professional book cover aesthetic
- Cinematic composition with strong focal point
- Rich colors and atmospheric lighting
- Commercial appeal for ${targetAudience}
- No text or titles on the image
- Modern publishing standards

Art style: Digital illustration, professional book cover art, trending on ArtStation`;

return {
  json: {
    bookId: book.id,
    title: book.title,
    prompt: coverPrompt.trim(),
    promptLength: coverPrompt.length
  }
};

