const response = $input.first().json;
const content = response.choices[0].message.content;
const bookData = $node["Prepare Cover Prompt"].json;

let refinedPrompt = content
  .replace(/```json\s*/g, '')
  .replace(/```\s*/g, '')
  .replace(/^[\"']|[\"']$/g, '')
  .replace(/{\"refined_prompt\":\s*\"/g, '')
  .replace(/\"}/g, '')
  .trim();

if (!refinedPrompt || refinedPrompt.length < 50) {
  refinedPrompt = bookData.prompt;
}

// Max length validation
if (refinedPrompt.length > 3900) {
  refinedPrompt = refinedPrompt.substring(0, 3900);
}

return {
  json: {
    refinedPrompt,
    bookId: bookData.bookId
  }
};

const items = $input.all();
return items.map(item => {
  const json = item.json;
  // Try different paths
  const url = json.url || (json.data && json.data[0] && json.data[0].url);

  if (!url) {
     return { json: { error: "No URL found", raw: json } };
  }

  return { json: { url } };
});

